    private void button_resetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_resetActionPerformed

        textUser.setText(null);
        txtPassword.setText(null);
    }//GEN-LAST:event_button_resetActionPerformed

    private void button_login1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_login1ActionPerformed
        // TODO add your handling code here:
        String user = textUser.getText();
        String password = txtPassword.getText();
        
        if (user.contains("User") && password.contains("0pass1")) {
            textUser.setText(null);
            txtPassword.setText(null);            
            
            new Menu().setVisible(true);
            dispose();
        }
        
        else
        {
            JOptionPane.showMessageDialog(null, "Invalid User or Password", "ERROR", JOptionPane.ERROR_MESSAGE);
                    textUser.setText(null);
                    txtPassword.setText(null);
        }        

    }//GEN-LAST:event_button_login1ActionPerformed

    private void button_exit1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_exit1ActionPerformed

        int opt = JOptionPane.showConfirmDialog(this, "Confirm to Exit",  "EXIT", JOptionPane.YES_NO_OPTION);
        if (opt == 0)
        System.exit(0);
        
        else
        {            
        }         
    }//GEN-LAST:event_button_exit1ActionPerformed

    private void showActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_showActionPerformed
        // TODO add your handling code here:
        if (show.isSelected())
        {
            txtPassword.setEchoChar((char)0);
        }
        else
            txtPassword.setEchoChar('*');
    }//GEN-LAST:event_showActionPerformed

