    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:     
                
        double sTotal = Double.parseDouble(stotal.getText());        
        double CakeTotal = Double.parseDouble(cake_total.getText()) ;
        double DrinksTotal = Double.parseDouble(drinks_total.getText());
        double FriedTotal = Double.parseDouble(fried_total.getText());
        double PizzaTotal = Double.parseDouble(pizza_total.getText());
        double SoupTotal = Double.parseDouble(soup_total.getText());
        double HDelivery = Double.parseDouble(HomeDl.getText());
        
        i[45] = CakeTotal+DrinksTotal+FriedTotal+PizzaTotal+SoupTotal+HDelivery+sTotal;
        
        String sub_total = String.format("%.2f", i[45]);
        stotal.setText(sub_total);
        
        double Tax = Double.parseDouble(stotal.getText());
        i[46] = (Tax * 2.5)/100;
        String VAT = String.format("%.2f", i[46]);
        vat.setText(VAT);
 
        
        double subTotal = Double.parseDouble(stotal.getText());
        double tax = Double.parseDouble(vat.getText());

        i[47] = subTotal+tax;
        String grand = String.format("%.2f", i[47]);
        gtotal.setText(grand);
               
        String CName = CustomerData.customer.getText();
        CustomerName.setText(CName);
        String CTable = CustomerData.table.getText();
        
        TableNO.setText(CTable);  
        //HomeDl.setText(CustomerData.home.getText());
        if (CustomerData.HD.isSelected()){
            HomeDl.setText("150.00");
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

